import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder }
  from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  myform: FormGroup = new FormGroup({})


  constructor(private fb: FormBuilder) { }



  createForm() {
    this.myform = this.fb.group({
      title: new FormControl('', [Validators.required, Validators.minLength(6)]),
      priority: new FormControl(null, [Validators.required]),
      done: new FormControl("", [Validators.required, Validators.minLength(6)])
    });
  }

  demo() {
    this.title
    debugger;
  }

  get title() {
    return this.myform.get('title') as FormControl
  }

  get priority() {
    return this.myform.get('priority') as FormControl
  }

  get done() {
    return this.myform.get('done') as FormControl
  }







  ngOnInit() {
    this.createForm();
  }






  name = 'TodoList App';
  todos = [
    {

      title: 'birthday',
      done: false,
      priority: 3
    },

    {
      title: 'birthda1',
      done: false,
      priority: 3
    },

    {

      title: 'birty',
      done: false,
      priority: 3
    }

  ];

  addTodo() {
    debugger
    const index = this.todos.findIndex(t => t.title === this.myform.value.title);
    if (index !== -1) {
      var newTodo = {
        title: this.myform.value.title,

        done: this.myform.value.done,
        priority: this.myform.value.priority,
      };
      this.todos[index] = newTodo;
    }
    else {
      var newTodo = {
        title: this.myform.value.title,

        done: this.myform.value.done,
        priority: this.myform.value.priority,
      };
      this.todos.push(newTodo);
    }


  }

  deleteTodo(todo: any) {

    this.todos = this.todos.filter(t => t.title !== todo.title);
  }
  UpdateTodo(todo: any) {
    this.myform.value.title = todo.title;
    this.myform.value.priority = todo.priority;
    this.myform.value.done = todo.done;
  }

  onSubmit() {
    console.log(this.myform?.value)
    this.myform.markAllAsTouched()
    this.addTodo()
  }


}
