import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(newTodoTitle: any,newTodoPriority1: any,newTodoDone1: any){
  var newTodo = {
    title: newTodoTitle,
    priority: newTodoPriority1,
    done: newTodoDone1
  };
  this.todos.push(newTodo);
}

deleteTodo(todo: { title: string; }){
  this.todos = this.todos.filter(t => t.title !== todo.title);
}
}
<h1>{{name}}</h1>

Title   : <input type= "text" #newTodo/><br><br>
Priority: <input type= "text" #newTodoPriority/><br><br>
Done    : <input type= "text" #newTodoDone/>
<button (click)="addTodo(newTodo.value, newTodoPriority.value, newTodoDone); newTodo.value= '' "> Add</button>

<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>    </td>
  </tr>
</table>
