import { Component,OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } 
    from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  myform: any;

  constructor(private fb : FormBuilder) {}


  title: any;
  priority: any;
  done: any;








  ngOnInit() {
    this.createForm();
  }


  createForm(){
    this.myform = new FormGroup({
      title: new FormControl('', Validators.required),
      priority: new FormControl( Validators.required),
      done: new FormControl( Validators.required)
    });
  }



  name = 'TodoList App';
  todos =[
   {

     title :'birthday',
    done : false,
    priority :3
   },

   {
    title :'birthda1',
    done : false,
    priority :3
   },

   {

    title :'birty',
    done : false,
    priority :3
   }

  ];

  addTodo(){

  const index = this.todos.findIndex(t => t.title === this.title);
  if (index !== -1) {
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}

onSubmit(){
  console.log(this.myform?.value)
  this.addTodo()
}


}
<app-navbar></app-navbar>
<h1>{{name}}</h1>


<form [formGroup]="myform">

      <label>Title:</label>
      <input type="text" formControlName="title" required minlength="6">
      <div *ngIf="title.invalid && (title.dirty || title.touched)"
    >

  <div *ngIf="title.errors?.['required']">
    Name is required.
  </div>
  <div *ngIf="title.errors?.['minlength']">
    Name must be at least 4 characters long.
  </div>

</div>

      <label>Priority:</label>
      <input type="text" formControlName="priority" required minlength="4">
      <div *ngIf="priority.invalid && (priority.dirty || priority.touched)"
      >

    <div *ngIf="priority.errors?.['required']">
      Name is required.
    </div>
    <div *ngIf="priority.errors?.['minlength']">
      Name must be at least 4 characters long.
    </div>

  </div>


    <label>Done:</label>
    <input type="text" formControlName="done">
    <div *ngIf="done.invalid && (done.dirty || done.touched)"
      >

    <div *ngIf="done.errors?.['required']">
      Name is required.
    </div>
    <div *ngIf="done.errors?.['minlength']">
      Name must be at least 4 characters long.
    </div>

  </div>

  <p>
      <button type="button" (click)="onSubmit()" >Submit</button>
  </p>
</form>


<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>
