import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } 
    from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title: any;
  priority: any;
  done: any;
  constructor(){}
  form = new FormGroup({
    title : new FormControl('',Validators.required),
    priority : new FormControl(Validators.required),
    done : new FormControl(Validators.required)
  })


  name = 'TodoList App';
  todos =[
   {

     title :'birthday',
    done : false,
    priority :3
   },

   {
    title :'birthda1',
    done : false,
    priority :3
   },

   {

    title :'birty',
    done : false,
    priority :3
   }

  ];

  addTodo(){

  const index = this.todos.findIndex(t => t.title === this.title);
  if (index !== -1) {
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}

onSubmit(){
  console.log(this.form.value)
  this.addTodo()
}


}
