import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder,ValidationErrors,ValidatorFn, AbstractControl }
  from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {

  myform: FormGroup = new FormGroup({});
  submitted = false;

  constructor(private fb: FormBuilder) { }
   identityRevealedValidator(): ValidatorFn {
    return (control: AbstractControl): ValidationErrors | null => {
      const password = control.get('password')?.value;
      const confirmPassword = control.get('confirmPassword')?.value;

      if (password !== confirmPassword) {
        return { passwordMismatch: true };
      }

      return null;
    };
  }
  createForm() {
    this.myform = this.fb.group({

      title: new FormControl('', [Validators.required]),
      firstName: new FormControl('', [Validators.required, Validators.minLength(8)]),
      lastName: new FormControl('', [Validators.required, Validators.minLength(8)]),
      dob: new FormControl("", [Validators.required, Validators.pattern('^\d{4}\-(0[1-9]|1[012])\-(0[1-9]|[12][0-9]|3[01])$')]),
      email: new FormControl("", [Validators.required, Validators.minLength(8)]),
      password: new FormControl("", [Validators.required, Validators.minLength(8)]),
      confirmPassword: new FormControl("", [Validators.required,this.identityRevealedValidator]),
      acceptTerms: new FormControl("", [Validators.required])
    });
  }

  // demo() {
  //   this.title
  //   debugger;
  // }

  // get title() {
  //   return this.myform.get('title') as FormControl
  // }

  // get priority() {
  //   return this.myform.get('priority') as FormControl
  // }

  // get done() {
  //   return this.myform.get('done') as FormControl
  // }


  ngOnInit() {
