<!-- main app container -->
<div class="card m-3">
  <h5 class="card-header">Angular Reactive driven form</h5>
  <div class="card-body">
      <form name="form" [formGroup]="myform"  (ngSubmit)="onSubmit()" >
          <div class="form-row">
              <div class="form-group col">
                  <label>Title</label>
                  <select name="title" class="form-control" formControlName="title" [ngClass]="{ 'is-invalid': submitted && title.invalid }" required  >
                      <option value=""></option>
                      <option value="Mr">Mr</option>
                      <option value="Mrs">Mrs</option>
                      <option value="Miss">Miss</option>
                      <option value="Ms">Ms</option>
                  </select>
                  <div *ngIf="submitted && title.invalid  " class ='invalid-feedback'>
                    <span *ngIf="title.errors['required']"> Title is Required </span>

                  </div>
              </div>
              <div class="form-group col-5">
                  <label>First Name</label>
                  <input type="text" name="firstName" class="form-control" formControlName="firstName" required [ngClass]="{ 'is-invalid': submitted && firstName.invalid }">
                  <div *ngIf="firstName.invalid && submitted" class="invalid-feedback">
                      <div *ngIf="firstName.errors['required']">First Name is required</div>
                      <div *ngIf="firstName.errors['minlength']">minimum character upto 8</div>
                  </div>



              </div>
            <div class="form-group col-5">
                  <label>Last Name</label>
                  <input type="text" name="lastName" class="form-control" formControlName="lastName" required [ngClass]="{ 'is-invalid': submitted && lastName.invalid }">
                  <div *ngIf="lastName.invalid && submitted" class="invalid-feedback" >
                    <div *ngIf="lastName.errors['required']">lastName  is required</div>
                    <div *ngIf="lastName.errors['minlength']">minimum character upto 8</div>
                </div>



            </div>
          </div>
          <div class="form-row">
              <div class="form-group col">
                  <label>Date of Birth</label>
                  <input type="date" name="dob" class="form-control" formControlName="dob" required [ngClass]="{ 'is-invalid': submitted && dob.invalid }">
                  <div *ngIf=" dob.invalid && submitted" class="invalid-feedback">
                      <div *ngIf="dob.errors['required']">Date of Birth is required</div>
                      <div *ngIf="dob.errors['pattern']">Date of Birth must be a valid date in the format YYYY-MM-DD</div>
                  </div>
              </div>
              <div class="form-group col">
                  <label>Email</label>
                  <input type="text" name="email" class="form-control" formControlName="email" required email [ngClass]="{ 'is-invalid': submitted && email.invalid }">
                  <div *ngIf="email.invalid && submitted" class="invalid-feedback">
                      <div *ngIf="email.errors['required']">Email is required</div>
                      <div *ngIf="email.errors['email']">Email must be a valid email address</div>
                  </div>
              </div>
          </div>
          <div class="form-row">
              <div class="form-group col">
                  <label>Password</label>
                  <input type="password" name="password" class="form-control" formControlName="password" required [ngClass]="{ 'is-invalid': submitted && password.invalid }">
                  <div *ngIf=" password.invalid && submitted" class="invalid-feedback">
                      <div *ngIf="password.errors['required']">Password is required</div>
                      <div *ngIf="password.errors['minlength']">Password must be at least 6 characters</div>
                  </div>
              </div>
              <div class="form-group col">
                  <label>Confirm Password</label>
                  <input type="password" name="confirmPassword" class="form-control" required formControlName="confirmPassword" [ngClass]="{ 'is-invalid': submitted && confirmPassword.invalid }">
                  <div *ngIf="confirmPassword.errors?.['identityRevealed'] && submitted" class="invalid-feedback">
                      <div *ngIf="confirmPassword.errors['required']">Confirm Password is required</div>
                    Passwords must match
                  </div>
              </div>d
          </div>
          <div class="form-group form-check">
              <input type="checkbox" name="acceptTerms" id="acceptTerms" class="form-check-input" formControlName="acceptTerms" required [ngClass]="{ 'is-invalid': submitted && acceptTerms.invalid }">
              <label for="acceptTerms" class="form-check-label">Accept Terms & Conditions</label>
              <div *ngIf=" acceptTerms.invalid && submitted" class="invalid-feedback">Accept Ts & Cs is required</div>
          </div>
          <div class="text-center">
              <button class="btn btn-primary mr-1">Register</button>
              <button class="btn btn-secondary" type="reset">Cancel</button>
          </div>
      </form>
  </div>
</div>
