import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  title = "";
  priority = "";
  done = "";

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(title: any,priority: any,done: any){
  debugger

  const index = this.todos.findIndex(t => t.title === title);
  if (index !== -1) {
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}


}
<app-navbar></app-navbar>
<h1>{{name}}</h1>

<form #todoForm="ngForm" (ngSubmit)="addTodo(title,priority, done)">
  <div class="form-group">
    <input type= "text" name="title" id = "title" ngModel #title="ngModel" required minlength="6" class="form-control"><br><br>
    <div *ngIf="title.invalid && (title.dirty || title.touched)"
      class="alert alert-danger">

    <div *ngIf="title.errors?.required">
      Name is required.
    </div>
    <div *ngIf="title.errors?.minlength">
      Name must be at least 4 characters long.
    </div>


  </div>
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="priority" /><br><br>
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="done"/> 
  </div>
  <!-- <button (click)="addTodo(title,priority, done)"> Add</button> -->
</form>



<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>

