import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  title = "";
  priority = "";
  done = "";

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(title: any,priority: any,done: any){
  debugger
  if(this.title == ""){return}
  const index = this.todos.findIndex(t => t.title === title);
  if (index !== -1) {
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}
todo: any = {
  title: '',
  description: ''
};

onSubmit(form: any): void {
  if (form.valid) {
    // Perform your form submission logic here
    console.log('Form submitted:', this.todo);
  }
}

}
<app-navbar></app-navbar>
<h1>{{name}}</h1>

<form>
  <div class="form-group">
    <input type= "text" [(ngModel)]="title" #newTodo /><br><br>
    <div *ngIf="title.invalid && (title.dirty || title.touched)"
      class="alert alert-danger">

    <div *ngIf="title.errors?.required">
      Name is required.
    </div>
    <div *ngIf="title.errors?.minlength">
      Name must be at least 4 characters long.
    </div>


  </div>
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="priority" #newTodoPriority/><br><br>
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="done" #newTodoDone/> 
  </div>
  <button (click)="addTodo(title,priority, done); newTodo.value= '' "> Add</button>
</form>



<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>

<form #todoForm="ngForm" (ngSubmit)="onSubmit(todoForm)">
  <div class = "form-group">


      <input type="text" id="title" name="title" required minlength="5" [(ngModel)]="todo.title"/>
      <div *ngIf="todo.title.invalid && (todo.title.dirty || todo.title.touched)"
      class="alert alert-danger">

    <div *ngIf="todo.title.errors?.required">
      Name is required.
    </div>
    <div *ngIf="todo.title.errors?.minlength">
      Name must be at least 4 characters long.
    </div>


  </div>
  </div>



  <div>
    <label for="description">Description:</label>
    <textarea id="description" name="description" required [(ngModel)]="todo.description"></textarea>
    <!-- <div *ngIf="todoForm.controls['description'].invalid && (todoForm.controls['description'].dirty || todoForm.controls['description'].touched)">
      <div *ngIf="todoForm.controls['description'].errors?['required']">Description is required.</div>
    </div> -->
  </div>

  <button type="submit" [disabled]="todoForm.invalid">Save</button>
</form>
