import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  title :any;
  priority :any;
  done :any;

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(){
  debugger

  const index = this.todos.findIndex(t => t.title === this.title);
  if (index !== -1) {
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}


}
<app-navbar></app-navbar>
<h1>{{name}}</h1>

<form #todoForm="ngForm" (ngSubmit)="addTodo()">
  <div class="form-group">
    <input type="text" name="titleControl1" #titleControl="ngModel" ngModel required minlength="6" maxlength="10" class="form-control"><br><br>
    {{titleControl}}
    <!-- <div *ngIf="titleControl?.invalid && (titleControl?.dirty || titleControl?.touched)"
      class="alert alert-danger">

    <div *ngIf="titleControl?.valid">
      Name is required.
    </div>
    <div *ngIf="titleControl?.errors.required">
      First Name is required
   </div>
    <div *ngIf="titleControl">
      Name must be at least 4 characters long.
    </div>


  </div> -->
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="priority" /><br><br>
  </div>
  <div class="form-group">
    <input type= "text" [(ngModel)]="done"/> 
  </div>
  <!-- <button (click)="addTodo(title,priority, done)"> Add</button> -->
</form>



<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>
