import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  titles = "";
  priority = "";
  done = "";

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(titles: any,priority: any,done: any){
  var newTodo = {
    title: titles,

    done: done,
    priority: priority,
  };

  this.todos.push(newTodo);
}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
// UpdateTodo(todo:any){
//   this.todos = todo;
//   this.priority = todo.priority
//   // this.todos = this.todos.filter(t => t.title !== todo.title);

// }
}
<app-navbar></app-navbar>
<h1>{{name}}</h1>


Title   : <input type= "text" [(ngModel)]="title" #newTodo /><br><br>
Priority: <input type= "text" [(ngModel)]="priority" #newTodoPriority/><br><br>
Done    : <input type= "text" [(ngModel)]="done" #newTodoDone/> 
<button (click)="addTodo(title,priority, done); newTodo.value= '' "> Add</button>

<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>    </td>
      <!-- <button (click)="UpdateTodo(todo)"> Update</button>     -->
  </tr>
</table>
