import { Component,OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } 
    from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  myform: FormGroup = new FormGroup({})


  constructor(private fb : FormBuilder) {}



  createForm(){
    this.myform = this.fb.group({

      title: new FormControl('', [Validators.required,Validators.minLength(6)]),
       priority: new FormControl('',[Validators.required]),
       done: new FormControl('', [Validators.required])
    });
  }






  ngOnInit() {
   this.createForm();
  }






  name = 'TodoList App';
  todos =[
   {

     title :'birthday',
    done : false,
    priority :3
   },

   {
    title :'birthda1',
    done : false,
    priority :3
   },

   {

    title :'birty',
    done : false,
    priority :3
   }

  ];

  addTodo(){
  debugger
  const index = this.todos.findIndex(t => t.title === this.myform.value.title);
  if (index !== -1) {
    var newTodo = {
      title: this.myform.value.title,

      done: this.myform.value.done,
      priority: this.myform.value.priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: this.myform.value.title,

      done: this.myform.value.done,
      priority: this.myform.value.priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.myform.value.title = todo.title;
this.myform.value.priority=todo.priority;
this.myform.value.done = todo.done;
}

onSubmit(){
  console.log(this.myform?.value)
  this.addTodo()
}


}
<app-navbar></app-navbar>
<h1>{{name}}</h1>


<form [formGroup]="myform">

      <label>Title:</label>
      <input type="text" formControlName="title" required minlength="6">


      <label>Priority:</label>
      <input type="text" formControlName="priority" required >


    <label>Done:</label>
    <input type="text" formControlName="done" required >

  <p>
      <button type="button" [disabled]="myform" (click)="addTodo()" >Submit</button>
  </p>
</form>


<table>

  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    debugger
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>
