import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  title :any;
  priority :any;
  done :any;

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

