import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(){}
  title = "";
  priority = "";
  done = "";

  name = 'TodoList App';
  todos =[
   {
    title: 'Birthday',
    done: false,
    priority: 3
   },

   {
    title: 'Meeting',
    done: true,
    priority: 1
   },

   {
    title: 'Pay Bill',
    done: true,
    priority: 2
   }

  ];

  addTodo(title: any,priority: any,done: any){
  debugger

  const index = this.todos.findIndex(t => t.title === title);
  if (index !== -1) {
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: title,

      done: done,
      priority: priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}


}
