import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } 
    from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title: any;
  priority: any;
  done: any;
  constructor(){}
  form = new FormGroup({
    title : new FormControl(''),
    priority : new FormControl(),
    done : new FormControl()
  })


  name = 'TodoList App';
  todos =[
   {

     title : new FormControl('Birthday'),
    done : new FormControl(false),
    priority : new FormControl(3)
   },

   {

    title : new FormControl('Meetinf'),
    done : new FormControl(true),
    priority : new FormControl(1)
   },

   {

    title : new FormControl('Pay Bill'),
    done : new FormControl(true),
    priority : new FormControl(2)
   }

  ];

  addTodo(){
  debugger

  const index = this.todos.findIndex(t => t.title === this.title);
  if (index !== -1) {
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos[index] = newTodo;
  }
  else{
    var newTodo = {
      title: this.title,

      done: this.done,
      priority: this.priority,
    };
    this.todos.push(newTodo);
  }


}

deleteTodo(todo:any){

  this.todos = this.todos.filter(t => t.title !== todo.title);
}
UpdateTodo(todo:any){
this.title = todo.title;
this.priority=todo.priority;
this.done = todo.done;
}


}
