<form [formGroup]="form" (ngSubmit)="onSubmit()">
  <p>
      <label>Title:</label>
      <input type="text" formControlName="title">
  </p>
  <p>
      <label>Priority:</label>
      <input type="password" formControlName="priority">
  </p>
  <p>
    <label>Done:</label>
    <input type="password" formControlName="done">
</p>
  <p>
      <button type="submit" [disabled]="!form.valid">Submit</button>
  </p>
</form>


<table>
  <tr>
    <th>Title</th>
    <th>Priority</th>
    <th>Done</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let todo of todos">
    <td>{{todo.title}}</td>
    <td>{{todo.priority}}</td>
    <td>{{todo.done ? '&#10003;' : '&#10005;'}}</td>
    <td>
      <button (click)="deleteTodo(todo)"> Delete</button>   
      <button (click)="UpdateTodo(todo)"> Update</button>    </td> 
  </tr>
</table>
